import base64
import os


def decode_base64_to_pdf(base64_data, output_filename):
    """
    将Base64编码的PDF数据解码并保存为PDF文件

    Args:
        base64_data (str): Base64编码的字符串（可以包含data:application/pdf;base64,前缀）
        output_filename (str): 输出的PDF文件名
    """
    try:
        # 检查并移除可能的数据URI前缀
        if base64_data.startswith('data:application/pdf;base64,'):
            base64_data = base64_data.split(',', 1)[1]

        # 解码Base64数据
        pdf_data = base64.b64decode(base64_data)

        # 写入PDF文件
        with open(output_filename, 'wb') as pdf_file:
            pdf_file.write(pdf_data)

        print(f"PDF文件已成功保存为: {output_filename}")
        print(f"文件大小: {len(pdf_data)} 字节")

        # 验证文件是否成功创建
        if os.path.exists(output_filename):
            print("文件验证: 成功")
        else:
            print("文件验证: 失败")

    except Exception as e:
        print(f"解码过程中出现错误: {str(e)}")


